import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInputGroup,
  MDBInput,
} from "mdbreact";

import { addAideToDB } from "../actions/aideActionCreator";
import { connect } from "react-redux";

class Postuler extends Component {
  state = {
    modal14: false,
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };

  render() {
    return (
      <MDBContainer>
        {/* **********modal********** */}
        <MDBBtn gradient="blue" onClick={this.toggle(14)}>
          Postuler maintenant
        </MDBBtn>
        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}>
Postuler maintena          </MDBModalHeader>
          <MDBModalBody>
            <MDBInput
              onChange={(e) => this.setState({ Nom_Prénom: e.target.value })}
              label="Nom Prénom"
              outline
            />
            <MDBInput
              onChange={(e) => this.setState({ Age: e.target.value })}
              label="Age"
              outline
            />
            <MDBInput
              onChange={(e) => this.setState({ photo: e.target.value })}
              label="Photo"
              outline
            />
            <MDBInputGroup
              containerClassName="mb-3"
              prepend="Sexe"
              inputs={
                <select
                  onChange={(e) => this.setState({ sexe: e.target.value })}
                  className="browser-default custom-select"
                >
                  <option value="0">Choisir...</option>
                  <option value="femme">Femme</option>
                  <option value="homme">Homme</option>
                </select>
              }
            />
            <MDBInput
              onChange={(e) => this.setState({ Num: e.target.value })}
              label="Numéro téléphone"
              outline
            />
            <MDBInput
              onChange={(e) => this.setState({ Ville: e.target.value })}
              label="Ville"
              outline
            />
            <MDBInput
              onChange={(e) => this.setState({ dispo: e.target.value })}
              label="Disponibilité"
              outline
            />

            <MDBInput
              onChange={(e) => this.setState({ Exp: e.target.value })}
              label="Expérience"
              outline
            />
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(14)}>
              Close
            </MDBBtn>
            <MDBBtn
              color="primary"
              onClick={() =>
                this.props.addAide({
                  Nom_Prénom: this.state.Nom_Prénom,
                  Age: this.state.Age,
                  photo: this.state.photo,
                  Ville: this.state.Ville,
                  Exp: this.state.Exp,
                  dispo: this.state.dispo,
                  Num: this.state.Num,
                  sexe: this.state.sexe,
                })
              }
            >
              Postuler
            </MDBBtn>
          </MDBModalFooter>
        </MDBModal>
        {/*************** **** card ****************/}
        <h1>Card Flip with Text</h1>
        <h3>Hover over the image below:</h3>

        <div className="flip-card">
          <div className="flip-card-inner">
            <div className="flip-card-front">
              <img
                className="card-aide"
                src="https://resize.prod.femina.ladmedia.fr/rblr/652,438/img/var/2015-09/comment-devenir-une-personne-solaire-0e95b16c5fefae293635ad5b6ce2b1198537e371.jpg"
                alt="Avatar"
              />
              <p>kkk</p>
            </div>
            <div className="flip-card-back">
              <h1>John Doe</h1>
              <p>Architect & Engineer</p>
              <p>We love that guy</p>
            </div>
          </div>
        </div>
      </MDBContainer>
    );
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    addAide: (el) => dispatch(addAideToDB(el)),
  };
};

export default connect(null, mapDispatchToProps)(Postuler);
