import React, { Component } from "react";
import "../App.css";
import { BrowserRouter as Router } from "react-router-dom";
import {
  MDBNavbar,
  MDBNavItem,
  MDBNavLink,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavbarToggler,
  MDBCollapse,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBInput,
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
} from "mdbreact";

import Inscription from "./inscription";

import { logout } from "../actions/auth";
import { connect } from "react-redux";

import { login } from "../actions/auth";

export class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
      modal: false,
    };
    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse,
    });
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  render() {
    console.log("z", this.props.user);
    const bgPink = { backgroundColor: "#ffffff" };
    return (
      <div>
        <Router>
          <header>
            <MDBNavbar style={bgPink} dark expand="md" scrolling fixed="top">
              <MDBNavbarBrand href="/">
                <strong style={{ color: "blue" }}>Navbar</strong>
              </MDBNavbarBrand>
              <MDBNavbarToggler onClick={this.onClick} />
              <MDBCollapse isOpen={this.state.collapse} navbar>
                <MDBNavbarNav left>
                  <MDBNavItem active>
                    <MDBNavLink style={{ color: "blue" }} to="/">
                      Accueil
                    </MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink style={{ color: "blue" }} to="/aides">
                      Aides ménagères
                    </MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink style={{ color: "blue" }} to="/client">
                      Pricing
                    </MDBNavLink>
                  </MDBNavItem>
                </MDBNavbarNav>
                <MDBNavbarNav right>
                  {this.props.user.role ? (
                    <button onClick={() => this.props.logout()}>logout</button>
                  ) : (
                    <MDBBtn gradient="blue" onClick={this.toggle}>
                      Connexion
                    </MDBBtn>
                  )}
                  {this.props.user.role ? "" : <Inscription />}
                </MDBNavbarNav>
              </MDBCollapse>
            </MDBNavbar>
          </header>
        </Router>

        <MDBContainer>
          <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
            <MDBModalHeader toggle={this.toggle}>Se connecter</MDBModalHeader>
            <MDBModalBody>
              <MDBContainer>
                <MDBRow>
                  <MDBCol md="6">
                    <form>
                      <div className="grey-text">
                        <i
                          class="fas fa-envelope"
                          style={{ fontSize: "30px" }}
                        ></i>
                        <MDBInput
                          style={{ color: "black" }}
                          label="Adresse email"
                          group
                          type="email"
                          validate
                          error="wrong"
                          success="right"
                          onChange={(e) =>
                            this.setState({ email: e.target.value })
                          }
                        />
                        <i class="fas fa-lock" style={{ fontSize: "30px" }}></i>
                        <MDBInput
                          style={{ color: "black" }}
                          label="Mot de passe"
                          group
                          type="password"
                          validate
                          error="wrong"
                          success="right"
                          onChange={(e) =>
                            this.setState({ password: e.target.value })
                          }
                        />
                      </div>
                    </form>
                  </MDBCol>
                </MDBRow>
              </MDBContainer>
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggle}>
                Fermer{" "}
              </MDBBtn>
              <MDBBtn
                gradient="blue"
                onClick={() =>
                  this.props.signin({
                    email: this.state.email,
                    password: this.state.password,
                  })
                }
              >
                Se connecter
                <MDBIcon far icon="paper-plane" className="ml-2" />
              </MDBBtn>
            </MDBModalFooter>
          </MDBModal>
        </MDBContainer>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    signin: (el) => dispatch(login(el)),
    logout: () => dispatch(logout()),
  };
};

export default connect(null, mapDispatchToProps)(Navbar);
