import React, { Component } from "react";
import OwlCarousel from "react-owl-carousel";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import { getRatingFromDB } from "../actions/ratingAction";
import { connect } from "react-redux";
let tab = []
export class CarouselRating extends Component {
  componentDidMount() {
    this.props.getRatingFromDB();

  }



  render() {
    tab = Array(5).fill(<p>ok</p>)
    return (
      <div className="carousel-rating">
        <OwlCarousel className="owl-theme" loop margin={10} nav>
          {this.props.rating.map((el) => 
            <>
              <div >
            <h4>{el.nom_prenom}</h4>
 <h3>{el.rating ===5 ? <><i class="fas fa-broom icon" style={{ color: "blue" }} ></i>
 <i class="fas fa-broom icon"  style={{ color: "blue" }} ></i>
 <i class="fas fa-broom icon" style={{ color: "blue" }}></i>
   <i
                        class="fas fa-broom icon"
                        style={{ color: "blue" }}
                      ></i><i
                      class="fas fa-broom icon"
                      style={{ color: "blue" }}
                    ></i> </> :  el.rating ===4 ? <><i
                                    class="fas fa-broom icon"
                                        style={{ color: "blue" }}
                                      ></i><i
                                      class="fas fa-broom icon"
                                      style={{ color: "blue" }}
                                    ></i><i
                                    class="fas fa-broom icon"
                                    style={{ color: "blue" }}
                                  ></i><i
                                  class="fas fa-broom icon"
                                  style={{ color: "blue" }}
                                ></i> </> :el.rating ===3? <> <i
                                            class="fas fa-broom icon"
                                            style={{ color: "blue" }}
                                          ></i><i
                                          class="fas fa-broom icon"
                                          style={{ color: "blue" }}
                                        ></i><i
                                        class="fas fa-broom icon"
                                        style={{ color: "blue" }}
                                      ></i> </>  :el.rating ===3? <> <i
                                                  class="fas fa-broom icon"
                                                  style={{ color: "blue" }}
                                                ></i><i
                                                class="fas fa-broom icon"
                                                style={{ color: "blue" }}
                                              ></i><i
                                              class="fas fa-broom icon"
                                              style={{ color: "blue" }}
                                            ></i> </> :el.rating ==2 ? <> <i
                                                  class="fas fa-broom icon"
                                                  style={{ color: "blue" }}
                                                ></i><i
                                                class="fas fa-broom icon"
                                                style={{ color: "blue" }}
                                              ></i> </>: <i
                                              class="fas fa-broom icon"
                                              style={{ color: "blue" }}
                                            ></i>  } </h3>

                                            <h4>{el.msg}</h4>

                                          </div>
                                            </> )}</OwlCarousel>
                    </div>)}}







const mapStateToProps = (state) => {
  return {
    rating: state.rating,
  };
};

export default connect(mapStateToProps, { getRatingFromDB })(CarouselRating);
