import {ADD_AIDE,GET_AIDE}from "../actions/types"
const initSate=[]
const aideReducer=(state=initSate,action)=>{
  if(action.type===ADD_AIDE){
  return [...state,action.payload]}
  if(action.type===GET_AIDE){
    return action
  }
  else{
      return state
  }
}
export default aideReducer;
