import React from "react";

import Rating from "react-rating";
import { connect } from "react-redux";
import { addRatingToDB } from "../../actions/ratingAction";
import { getUser } from "../../actions/auth";
import {
  MDBInput,
  MDBMask,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBBtn,
  MDBView,
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBAnimation,
} from "mdbreact";

class Avis extends React.Component {
  state = {
    collapseID: "",

    value: 0,
  };
  handleClick = () => {
    this.setState({ value: 0 });
  };
  send = (event) => {
    this.setState({ value: event });
  };

  toggleCollapse = (collapseID) => () =>
    this.setState((prevState) => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : "",
    }));
  componentDidMount() {
    this.props.getUser();
  }
  addStar = () => {
    this.props.addRatingToDB({
      id_client: this.state.id_client,
      nom_prenom: this.state.nom_prenom,
      rating: this.state.rating,
      msg: this.state.msg,
    });
  };
  render() {
    return (

      <div className="avis">
        <div id="classicformpage">
          <MDBView>
            <MDBMask className="d-flex justify-content-center align-items-center gradient">
              <MDBContainer>
                <MDBRow>
                  <MDBAnimation
                    type="fadeInLeft"
                    delay=".3s"
                    className="white-text text-center text-md-left col-md-6 mt-xl-5 mb-5"
                  >
                    <MDBCard id="classic-card">
                      <MDBCardBody className="white-text">
                        <h3 className="text-center">
                          <MDBIcon icon="user" /> Mon profil
                        </h3>
                        <hr className="hr-light" />

                        <MDBInput
                          className="white-text"
                          iconClass="white-text"
                          label="Nom-PrÃ©nom"
                          icon="user"
                          onChange={(e) =>
                            this.setState({ nom_prenom: e.target.value })
                          }
                        />

                        <MDBInput
                          className="white-text"
                          type="textarea"
                          label="Message"
                          rows="2"
                          icon="pencil-alt"
                          onChange={(e) =>
                            this.setState({ msg: e.target.value })
                          }
                        />

                        <div className="text-center mt-4 black-text">
                          <MDBBtn color="indigo" onClick={this.addStar}>
                            Envoyer{" "}
                          </MDBBtn>
                          <hr className="hr-light" />
                        </div>
                      </MDBCardBody>
                    </MDBCard>
                  </MDBAnimation>

                  <MDBCol
                    md="6"
                    xl="5"
                    className="mb-4"
                    style={{ height: "500px" }}
                  >
                    <MDBAnimation type="fadeInRight" delay=".2s">
                      <h1 className="h1-responsive font-weight-bold">
                        Sign up right now!
                      </h1>
                      <hr className="hr-light" />
                      <h6 className="mb-4">
                        Lorem ipsum dolor sit amet, consectetur adipisicing
                        elit. Rem repellendus quasi fuga nesciunt dolorum nulla
                        magnam veniam sapiente, fugiat! Commodi sequi non animi
                        ea dolor molestiae, quisquam iste, maiores. Nulla.
                      </h6>
                      <div className="rating" style={{fontSize:50}}>
        <Rating
          placeholderRating={1}
          emptySymbol={<i class="fas fa-broom icon"></i>}
          placeholderSymbol={
            <i class="fas fa-broom icon" style={{ color: "grey" }}></i>
          }
          fullSymbol={
            <i class="fas fa-broom icon" style={{ color: "blue" }}></i>
          }
          initialRating={this.state.value}
            onChange={this.send}
        />
<div className="button-rating">
        <button onClick={this.handleClick}>Reset</button>
        </div>
      </div>
                    </MDBAnimation>
                  </MDBCol>
                </MDBRow>
              </MDBContainer>
            </MDBMask>
          </MDBView>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    user: state.auth,
    rating: state.rating,
  };
};

export default connect(mapStateToProps, { getUser, addRatingToDB })(Avis);
