import { ADD_AIDE, GET_AIDE } from "./types";
import axios from "axios";
/************POST********** */
export const addAide = (payload) => {
  return {
    type: ADD_AIDE,
    payload,
  };
};
export const addAideToDB = (el) => {

  return (dispatch) =>
    axios
      .post(`http://localhost:8000/chef-d'oeuvre/aides/add`, el)
      .then((res) =>
        dispatch(addAide(res.data), window.location.reload(false))
      );
};
