                Contactez-nous{" "}
                <i
                  class="fa fa-quote-right"
                  style={{ color: "rgba(29, 210, 177, 0.7)", fontSize: 30 }}
                ></i>
              </p>
            </span>
          </h1>
        </div>

        {/*************************** * cards horizontales ***********************/}
