  componentDidMount() {
    this.props.getAideFromDB();
    this.props.getUser();
  }
  toggleReservation = () => {
    this.setState({
      modal3: !this.state.modal3,
    });
  };
  toggle = () => {
    this.setState({
      modal: !this.state.modal,
