import React from "react";
import { connect } from "react-redux";
import { getUser, editUserInDB } from "../actions/auth";
import {
  MDBMask,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBBtn,
  MDBView,
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBAnimation,
} from "mdbreact";
import EditProfil from "./edit";

class Profil extends React.Component {
  state = {
    collapseID: "",
    // nom_prenom: this.props.user.nom_prenom,
    // email: this.props.user.email,
    // password: this.props.user.password,
  };

  toggleCollapse = (collapseID) => () =>
    this.setState((prevState) => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : "",
    }));
  componentDidMount() {
    this.props.getUser();
  }
  test = () => {
    this.props.editUserInDB({
      _id: this.props.user._id,
      nom_prenom: this.state.nom_prenom,
      email: this.state.email,
      password: this.state.password,
      role: this.props.user.role,
    });
  };
  render() {
    console.log(this.state.email);
    const overlay = (
      <div
        id="sidenav-overlay"
        style={{ backgroundColor: "transparent" }}
        onClick={this.toggleCollapse("navbarCollapse")}
      />
    );
    return (
      <div id="classicformpage">
        <MDBView>
          <MDBMask className="d-flex justify-content-center align-items-center gradient">
            <MDBContainer>
              <MDBRow>
                <MDBAnimation
                  type="fadeInLeft"
                  delay=".3s"
                  className="white-text text-center text-md-left col-md-6 mt-xl-5 mb-5"
                >
                  <h1 className="h1-responsive font-weight-bold">
                    Sign up right now!
                  </h1>
                  <hr className="hr-light" />
                  <h6 className="mb-4">
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                    Rem repellendus quasi fuga nesciunt dolorum nulla magnam
                    veniam sapiente, fugiat! Commodi sequi non animi ea dolor
                    molestiae, quisquam iste, maiores. Nulla.
                  </h6>
                  <EditProfil />
                </MDBAnimation>

                <MDBCol
                  md="6"
                  xl="5"
                  className="mb-4"
                  style={{ height: "500px" }}
                >
                  <MDBCard id="classic-card">
                    <MDBCardBody className="white-text">
                      <h3 className="text-center">
                        <MDBIcon icon="user" /> Register:
                      </h3>
                      <hr className="hr-light" />
                      <MDBInput
                        className="white-text"
                        iconClass="white-text"
                        //label={this.props.user.nom_prenom}
                        icon="user"
                        valueDefault={
                          this.props.user.nom_prenom
                            ? this.props.user.nom_prenom
                            : null
                        }
                        onChange={(e) =>
                          this.setState({ nom_prenom: e.target.value })
                        }
                      />
                      <MDBInput
                        className="white-text"
                        iconClass="white-text"
                        label="Your email"
                        icon="envelope"
                        valueDefault={this.props.user.email}
                        onChange={(e) =>
                          this.setState({ email: e.target.value })
                        }
                      />
                      <MDBInput
                        className="white-text"
                        iconClass="white-text"
                        //  label="Your password"
                        icon="lock"
                        type="password"
                        value={this.props.user.password}
                        onChange={(e) =>
                          this.setState({ password: e.target.value })
                        }
                      />

                      <div className="text-center mt-4 black-text">
                        <MDBBtn color="indigo" onClick={this.test}>
                          Modifier
                        </MDBBtn>
                        <hr className="hr-light" />
                        <div className="text-center d-flex justify-content-center white-label">
                          <a href="#!" className="p-2 m-2">
                            <MDBIcon
                              fab
                              icon="twitter"
                              className="white-text"
                            />
                          </a>
                          <a href="#!" className="p-2 m-2">
                            <MDBIcon
                              fab
                              icon="linkedin"
                              className="white-text"
                            />
                          </a>
                          <a href="#!" className="p-2 m-2">
                            <MDBIcon
                              fab
                              icon="instagram"
                              className="white-text"
                            />
                          </a>
                        </div>
                      </div>
                    </MDBCardBody>
                  </MDBCard>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBMask>
        </MDBView>

        <MDBContainer>
          <MDBRow className="py-5">
            <MDBCol md="12" className="text-center">
              <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
                eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
                enim ad minim veniam, quis nostrud exercitation ullamco laboris
                nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor
                in reprehenderit in voluptate velit esse cillum dolore eu fugiat
                nulla pariatur. Excepteur sint occaecat cupidatat non proident,
                sunt in culpa qui officia deserunt mollit anim id est laborum.
              </p>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    user: state.auth,
  };
};

export default connect(mapStateToProps, { getUser, editUserInDB })(Profil);
