import React, { Component } from "react";

import {
  MDBCarousel,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBMask,
  MDBContainer,
  MDBBtn,
  MDBIcon,
} from "mdbreact";
import { getAideFromDB } from "../actions/aideActionCreator";
import Navbar from "./navbar";
import { connect } from "react-redux";

export class Accueil extends Component {
  // componentDidMount() {
  //   this.props.getAide();
  // }
  render() {
    return (
      <div className="accueil">
        <Navbar />
        {/* *****carousel *********/}
        <div className="caroussel">
          <MDBContainer>
            <MDBCarousel
              activeItem={1}
              length={3}
              showControls={true}
              showIndicators={true}
              className="z-depth-1"
            >
              <MDBCarouselInner>
                <MDBCarouselItem itemId="1">
                  <MDBView>
                    <img
                      className="d-block w-100"
                      src="https://mdbootstrap.com/img/Photos/Slides/img%20(68).jpg"
                      alt="First slide"
                    />
                    <MDBMask overlay="black-light" />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className="h3-responsive">Light mask</h3>
                    <p>First text</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>
                <MDBCarouselItem itemId="2">
                  <MDBView>
                    <img
                      className="d-block w-100"
                      src="https://mdbootstrap.com/img/Photos/Slides/img%20(6).jpg"
                      alt="Second slide"
                    />
                    <MDBMask overlay="black-strong" />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className="h3-responsive">Strong mask</h3>
                    <p>Second text</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>
                <MDBCarouselItem itemId="3">
                  <MDBView>
                    <img
                      className="d-block w-100"
                      src="https://mdbootstrap.com/img/Photos/Slides/img%20(9).jpg"
                      alt="Third slide"
                    />
                    <MDBMask overlay="black-slight" />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className="h3-responsive">Slight Mast</h3>
                    <p>Third text</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>
              </MDBCarouselInner>
            </MDBCarousel>
          </MDBContainer>
        </div>
        {/************ * les filtres ***************/}
        <div className="filtre container">
          <select
            className="browser-default custom-select"
            style={{ width: "200px" }}
          >
            <option>C</option>
            <option value="1">Option 1</option>
            <option value="2">Option 2</option>
            <option value="3">Option 3</option>
          </select>
          <select
            className="browser-default custom-select"
            style={{ width: "200px" }}
          >
            <option>Choose your option</option>
            <option value="1">Option 1</option>
            <option value="2">Option 2</option>
            <option value="3">Option 3</option>
          </select>
        </div>
        {/* ***********cards *******************/}
        <div className="cards container">
          {/* {this.props.aides.map((el) => ( */}

          <div className="flip-card">
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <img
                  className="card-aide"
                  src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRBzZjxx-KhtwQSK56K2dg-q4SoW9ssY6cCvg&usqp=CAU"
                  alt="Avatar"
                />
                <br></br>
                <h2>Mariem Chatteli</h2>
                <hr className="line"></hr>
                <h5>
                  Plus de détails <MDBIcon icon="angle-double-right" />
                </h5>
              </div>
              <div className="flip-card-back">
                <h1>Age</h1>
                <p>Ville</p>
                <p>Exp</p>
                <MDBBtn outline color="info">
                  Réserver
                  <MDBIcon far icon="paper-plane" />
                </MDBBtn>
              </div>
            </div>
          </div>
          {/* </>
          ))} */}
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getAide: () => dispatch(getAideFromDB()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Accueil);
