import React, { Component } from "react";
import { connect } from "react-redux";
import { getAideFromDB } from "../../actions/aideActionCreator";
import {
  getReservation,
  deleteReservationInDB,
} from "../../actions/reservationAction";
import { getUser } from "../../actions/auth";
import { Label, Table, Button } from "semantic-ui-react";

export class ReservationAttente extends Component {
  state = {
    page: 1,
    pageSize: 3,
  };
  componentDidMount() {
    this.props.getUser();
    this.props.getAideFromDB();
    this.props.getReservation();
  }
  // handle pagination
  page = (page, pageSize) => {
    this.setState({ page: page, pageSize: pageSize });
  };
  render() {
    const reservations = this.props.reservation.filter(
      (el) => el.client._id.toString() === this.props.user._id.toString()
    );

    console.log("u", reservations);
    return (
      <div className="liste-reservation container">
        <h1 className="reservartion-client">Mes rÃ©servations</h1>

        {reservations.length ? (
          <Table celled className="table-reserv-client">
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Nom-PrÃ©nom-Aide</Table.HeaderCell>
                <Table.HeaderCell>DÃ©lÃ©gation</Table.HeaderCell>
                <Table.HeaderCell>Service</Table.HeaderCell>
                <Table.HeaderCell>Jour</Table.HeaderCell>
                <Table.HeaderCell>ExpÃ©rience</Table.HeaderCell>
                <Table.HeaderCell>Age</Table.HeaderCell>
                <Table.HeaderCell>Devis</Table.HeaderCell>
                <Table.HeaderCell>Actions</Table.HeaderCell>
              </Table.Row>
            </Table.Header>

            <Table.Body>
              {reservations // pagination
                .filter(
                  (l, index) =>
                    (this.state.page - 1) * this.state.pageSize <= index &&
                    index < this.state.page * this.state.pageSize
                )
                .map((el, i) => (
                  <Table.Row>
                    <Table.Cell>
                      <Label ribbon>{el.nom}</Label>
                    </Table.Cell>
                    <Table.Cell>{el.ville}</Table.Cell>
                    <Table.Cell>{el.service}</Table.Cell>
                    <Table.Cell>{el.dispo}</Table.Cell>
                    <Table.Cell>{el.exp}</Table.Cell>
                    <Table.Cell>{el.age}</Table.Cell>
                    <Table.Cell>
                      {el.decision === "ValidÃ©e" ? (
                        <img
                          src={"http://localhost:8000/" + el.devis}
                          width="50px"
                        />
                      ) : (
                        el.decision
                      )}
                    </Table.Cell>

                    <Table.Cell className="pos-Action ">
                      {/* <Button secondary>
                      <ModifEvent el={el} />
                    </Button> */}

                      <Button
                        outline
                        size="sm"
                        onClick={() => this.props.deleteReservationInDB(el._id)}
                      >
                        <i class="fas fa-trash"></i>
                      </Button>
                    </Table.Cell>
                  </Table.Row>
                ))}{" "}
            </Table.Body>

            <Table.Footer>
              <Table.Row>
                <Table.HeaderCell colSpan="3"></Table.HeaderCell>
              </Table.Row>
            </Table.Footer>
          </Table>
        ) : (
          <h1>pas de reservations</h1>
        )}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
    user: state.auth,
    reservation: state.reservation,
  };
};

export default connect(mapStateToProps, {
  getAideFromDB,
  getReservation,
  getUser,
  deleteReservationInDB,
})(ReservationAttente);
