import {ADD_AIDE,}from "../actions/types"
const initSate=[]
const aideReducer=(state=initSate,action)=>{
  if(action.type===ADD_AIDE){
  return [...state,action.payload]}
  else{
      return state
  }
}
export default aideReducer;
