import axios from "axios";
import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  LOGIN_SUCCESS,
  // LOGOUT
} from "./types";
 import setAuthToken from "./setToken";

export function register(el) {
  console.log(el);
  return (dispatch) =>
    axios
      .post("http://localhost:8000/chef-d'oeuvre/authentif/register", el)
      .then((res) => dispatch({ type: REGISTER_SUCCESS, payload: res.data }))
      .catch((err) => console.log(err));
}

export function login  (email, password) {
  return(dispatch)=>
   axios.post("http://localhost:8000/chef-d'oeuvre/authentif/login", { email, password }).then(res=>
   dispatch({
    type: LOGIN_SUCCESS,
    payload: res.data
  }),dispatch(getUser())

   ).catch(err=> console.log(err))




};
export const getUser = ()=>async dispatch=> {
  if (localStorage.token) {
    setAuthToken(localStorage.token);
  }
  try {
    const res = await axios.get("http://localhost:8000/chef-d'oeuvre/authentif/profil");
    dispatch({
      type: USER_LOADED,
      payload: res.data
    });
  } catch (err) {
    console.log(err)
  }
};
