import AdminCalendar from "./AdminCalender";
import {
  getReservation,
  editDecisionInDB,
} from "../../actions/reservationAction";
import ModalcalenderAdmin from "./modalcalender";
import ModalDevis from "./modalDevis";
//import {addDisabledDays, updateSalle} from "../Redux/actions/reservation"
class GestionReserv extends Component {
  state = {
    decision: "Valider",
    devis: "",
    disabled: "disabled Days",
    valid: "true",
  };
  toggle() {
    this.setState({ disabled: "enable Days" });
  }

  componentDidMount() {
    this.props.getReservations();
  }
  handleInput = (e) => {
    this.setState({ devis: e.target.files[0] });
    console.log("devis", e.target.files[0].name);
  };

  upload = (e) => {
    const formData = new FormData();
    formData.append("devis", this.state.devis);
    axios.post("http://localhost:8000/", formData).then((res) => {
      console.log(this.state.devis.name);
    });
  };

  render() {
    return (
      <>
        {this.props.reservations ? (
          <MDBTable>
            <MDBTableHead color="elegant-color-dark" textWhite>
              <tr>
                <th>Nom</th>
                <th>Phone</th>
                <th>Entreprise</th>
                <th>Locataire</th>
                <th>salle</th>
                <th>Date</th>
                <th>personnes</th>
                <th>Evenement</th>
                <th>Decision</th>
                <th>Valider</th>
                <th>Ajouter disabled days</th>
              </tr>
            </MDBTableHead>
            <MDBTableBody>
              {this.props.reservations.map((el) => (
                <tr>
                  <td>{el.nomUser}</td>
                  <td>{el.phoneUser}</td>
                  <td>{el.etsUser}</td>
                  <td>{el.roleUser}</td>
                  <td>{el.nomSalle}</td>
                  <td>
                    {/* <AdminCalendar startDate={el.startDate} endDate={el.endDate}/> */}
                    <ModalcalenderAdmin
                      startDate={el.startDate}
                      endDate={el.endDate}
                    />
                  </td>
                  <td>{el.nbre_personne}</td>
                  <td>{el.evenement}</td>
                  <td>
                    {el.roleUser === "userExterne" &&
                    el.decision !== "en attente" ? (
                      <ModalDevis image={el.decision} />
                    ) : (
                      el.decision
                    )}
                  </td>
                  <td>
                    {el.roleUser === "userExterne" ? (
                      <>
                        <div className="custom-file">
                          <input
                            onChange={(e) => {
                              this.handleInput(e);
                            }}
                            type="file"
                            className="custom-file-input"
                            id="inputGroupFile01"
                            aria-describedby="inputGroupFileAddon01"
                          />
                          <label
                            className="custom-file-label"
                            htmlFor="inputGroupFile01"
                          >
                            Choose file
                          </label>

                          <>
                            <MDBBtn
                              className="btn-sm"
                              color="elegant"
                              onClick={() => (
                                this.upload(),
                                this.props.updateReservation(
                                  el._id,
                                  this.state.devis.name
                                )
                              )}
                            >
                              Devis
                            </MDBBtn>
                          </>
                        </div>
                      </>
                    ) : (
                      <MDBBtn
                        className="btn-sm"
                        outline
                        color="success"
                        onClick={() =>
                          this.props.updateReservation(
                            el._id,
                            this.state.decision
                          )
                        }
                      >
                        Valider
                      </MDBBtn>
                    )}
                  </td>
                  <td>
                    <MDBBtn
                      // btn-blue-grey
                      outline
                      color="danger"
                      className="btn-sm"
                      onClick={() => (
                        this.toggle(), this.props.deleteReservation(el._id)
                      )}
                    >
                      spprimer
                    </MDBBtn>
                  </td>
                </tr>
              ))}
            </MDBTableBody>
          </MDBTable>
        ) : (
          <div className="loader"></div>
        )}
      </>
    );
  }
}
const mapStateToProps = (state) => ({
  reservations: state.reservationReducer.reservations,
});

export default connect(mapStateToProps, {
  getReservation,
  editDecisionInDB,
})(GestionReserv);
