import { ADD_AIDE, GET_AIDE } from "./types";
import axios from "axios";
/************POST********** */
export const addAide = (payload) => {
  return {
    type: ADD_AIDE,
    payload,
  };
};

// export const addAideToDB = (el) => {
//   return (dispatch) =>
//     axios
//       .patch(`http://localhost:8000/aides/updateuser/${el.id}`, el)
//       .then((res) =>
//         dispatch(addAide(res.data), window.location.reload(false))
//       );
// };
/************GET********** */
export const getAide = (payload) => {
  return {
    type: GET_AIDE,
    payload,
  };
};
export const getAideFromDB = () => {
  return (dispatch) =>
    axios
      .get("http://localhost:4000/aides")
      .then((res) => dispatch(getAide(res.data)));
};
