import React, { Component } from "react";
import "./App.css";
import Postuler from "./components/dashboardAide/postuler-aide";
import Accueil from "./components/accueil";
import GestionReservation from "./components/dashboardAide/gestion-reservation";
import GestionUsers from "./components/dashbordAdmin/gestion-users";
import Connexion from "./components/connexion-navbar";
import { getAideFromDB } from "./actions/aideActionCreator";
import { getUser } from "./actions/auth";
import { login } from "./actions/auth";

import Navbar from "./components/navbar";
import { connect } from "react-redux";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

export class App extends Component {
  componentDidMount() {
    this.props.getUser();
  }
  render() {
    return (
      <div className="App">
        <Router>
          <div className="navbar">
            <Navbar user={this.props.user} />
          </div>
          <div>
            <Switch>
              <Route exact path="/">
                <Accueil />
              </Route>
              <Route path="/aide">
                <Postuler />
              </Route>
              <Route path="/gestion-reservation">
                <GestionReservation />
              </Route>
              <Route path="/gestion-users">
                <GestionUsers />
              </Route>

              <Route path="/login">
                <Connexion />
              </Route>
            </Switch>
          </div>
        </Router>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
    user: state.auth,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    signin: (el) => dispatch(login(el)),
    getUser: () => dispatch(getUser()),
    getAideFromDB: () => dispatch(getAideFromDB()),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(App);
