import React, { Component } from "react";
import { connect } from "react-redux";
import { getAideFromDB } from "../../actions/aideActionCreator";
import {
  getReservation,
  editDecisionInDB,
} from "../../actions/reservationAction";
import { getUser } from "../../actions/auth";
import { Label, Table, Button } from "semantic-ui-react";

export class GestionReservation extends Component {
  state = {
    decision: "Validée",
    deci: "Annulée",
  };
  componentDidMount() {
    this.props.getAideFromDB();
    this.props.getReservation();
    this.props.getUser();
  }
  render() {
    const reservations = this.props.reservation.filter(
      (el) => el.aide._id.toString() === this.props.user._id.toString()
    );

    return (
      <div className="liste-reservation container">
        <Table celled>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Date-début</Table.HeaderCell>
              <Table.HeaderCell>Date-fin</Table.HeaderCell>
              <Table.HeaderCell>Adresse</Table.HeaderCell>
              <Table.HeaderCell>Jour</Table.HeaderCell>
              <Table.HeaderCell>Decision</Table.HeaderCell>
              <Table.HeaderCell>Actions</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            {reservations /*.filter((el_organizateurs) => this.el_organizateurs === "" ?
                            el_organizateurs : el_organizateurs === "maazza"
        )*/
              .map((el, i) => (
                <Table.Row>


                  <Table.Cell>

                  (
                      <>
                       <Table.Cell>
                    {el.date_start}

                  </Table.Cell>
                   <Table.Cell>
                    {el.date_end}

                  </Table.Cell>
                        <p>{el.tel_client}</p>
                        <p>{el.adresse_client}</p>
                        <p>{el.client.nom_prenom}</p>
                      </>
                    ) }
                  </Table.Cell>

                  <Table.Cell className="pos-Action ">
                    <Button
                      outline
                      size="sm"
                      onClick={() =>
                        this.props.editDecisionInDB({
                          _id: el._id,
                          decision: this.state.decision,
                        })
                      }
                    >
                      <i class="fas fa-check-square"></i>
                    </Button>
                    <Button
                      outline
                      size="sm"
                      onClick={() =>
                        this.props.editDecisionInDB({
                          _id: el._id,
                          decision: this.state.deci,
                        })
                      }
                    >
                      <i class="fas fa-trash"></i>
                    </Button>
                  </Table.Cell>
                  <Button outline size="sm" className="but-etat">
                      {el.decision == "Validée définitive" ? (
                        <i
                          class="fas fa-check-circle vert"
                          style={{ color: "green" }}
                        ></i>
                      ) : (
                        ""
                      )}
                      {el.decision == "Annulée" ? (
                        <i
                          class="fas fa-minus-circle"
                          style={{ color: "red" }}
                        ></i>
                      ) : (
                        ""
                      )}
                    </Button>
                </Table.Row>
              ))}{" "}
          </Table.Body>

          <Table.Footer>
            <Table.Row>
              <Table.HeaderCell colSpan="3"></Table.HeaderCell>
            </Table.Row>
          </Table.Footer>
        </Table>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
    reservation: state.reservation,
    user: state.auth,
  };
};
export default connect(mapStateToProps, {
  getAideFromDB,
  getReservation,
  getUser,
  editDecisionInDB,
})(GestionReservation);
