import React, { Component } from "react";
import { editAideInDB } from "../../actions/aideActionCreator";
import { getUser } from "../../actions/auth";
import { connect } from "react-redux";
import axios from "axios";
import {
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInputGroup,
  MDBInput,
  MDBIcon,
} from "mdbreact";

export class EditAide extends Component {
  state = {
    _id: "",
    nom: "",
    age: "",
    photo: "",
    ville: "",
    exp: "",
    dispo: "",
    num: "",
    sexe: "",
    service: "",
    modal14: false,
  };
  /************get user + get aides*****/

  componentDidMount() {
    this.props.getUser();
  }
  /***************************** */
  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
      // nom: this.props.aides
      //   .filter((el) => el.proprietaire === this.props.user._id)
      //   .map((el) => el.nom)
      //   .join(),
    });
  };
  /*************function upload img*********** */
  fileSelectedHandler = (e) => {
    this.setState({ selectedFile: e.target.files[0] });
  };

  uploadHandler = () => {
    const fd = new FormData();
    fd.append("file", this.state.selectedFile);
    axios
      .post("http://localhost:8000/image", fd)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };
  render() {
    const villes = [
      "Bab El Bhar",
      "Bab Souika",
      "Carthage",
      "Cité El Khadra",
      "Sidi Hassine",
      "Sidi El Béchir",
      "Séjoumi",
      "Médina",
      "Le Kram",
      "Le Bardo",
      "La Marsa",
      "La Goulette",
      "Hraïria",
      "Ezzouhour",
      "Ettahrir",
      "Ouardia",
      "El Omrane supérieur",
      "El Omrane",
      "El Menzah",
      "El Kabaria",
      "Djebel Jelloud",
    ];
    return (
      <div className="edit-aide">
        <center>
          <button class="primary" onClick={this.toggle(14)}>
            Modifier
          </button>
          {/* <MDBBtn
            gradient="blue"
            onClick={this.toggle(14)}
            style={{ borderRadius: "20px" }}
          >
            Modifier{" "}
          </MDBBtn> */}
        </center>
        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}>
            Postuler maintenant{" "}
          </MDBModalHeader>
          <h1>amina</h1>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(14)}>
              Fermer
            </MDBBtn>
            <MDBBtn
              gradient="blue"
              onClick={() =>
                this.props.editAide({
                  _id: this.state.aides._id,
                  nom: this.state.nom,
                  age: this.state.age,
                  photo: this.state.selectedFile.name,
                  ville: this.state.ville,
                  exp: this.state.exp,
                  dispo: this.state.dispo,
                  num: this.state.num,
                  sexe: this.state.sexe,
                  service: this.state.service,
                })
              }
            >
              Modifier
              <MDBIcon far icon="paper-plane" className="ml-2" />
            </MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
    user: state.auth,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getUser: () => dispatch(getUser()),
    editAide: (id) => dispatch(editAideInDB(id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(EditAide);
