import React, { Component } from "react";
import { connect } from "react-redux";
import { editUserInDB, getUser } from "../actions/auth";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInputGroup,
} from "mdbreact";

class EditProfil extends Component {
  state = {
    modal8: false,
    modal9: false,
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };
  componentDidMount() {
    this.props.getUser();
  }

  render() {
    return (
      <MDBContainer>
        <MDBBtn color="info" onClick={this.toggle(8)}>
          Right
        </MDBBtn>
        <MDBModal
          isOpen={this.state.modal8}
          toggle={this.toggle(8)}
          fullHeight
          position="right"
        >
          <MDBModalHeader toggle={this.toggle(8)}>
            MDBModal title
          </MDBModalHeader>
          <MDBModalBody>
            <MDBContainer>
              <MDBInputGroup
                valueDefault={this.props.user.nom_prenom}
                onChange={(e) => this.setState({ nom_prenom: e.target.value })}
                material
                containerClassName="mb-2 mt-0"
                prepend="Nom-prÃ©nom"
              />
              <MDBInputGroup
                valueDefault={this.props.user.email}
                onChange={(e) => this.setState({ email: e.target.value })}
                material
                containerClassName="mb-2 mt-0"
                prepend="Email"
              />
              <MDBInputGroup
                material
                containerClassName="mb-2 mt-0"
                prepend="Mot de passe"
              />
            </MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(8)}>
              Close
            </MDBBtn>
            <MDBBtn color="primary">Save changes</MDBBtn>
          </MDBModalFooter>
        </MDBModal>

        <MDBModal
          isOpen={this.state.modal9}
          toggle={this.toggle(9)}
          fullHeight
          position="bottom"
        >
          <MDBModalHeader toggle={this.toggle(9)}>
            MDBModal title
          </MDBModalHeader>
          <MDBModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat.
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(9)}>
              Close
            </MDBBtn>
            <MDBBtn
              color="primary"
              onClick={() =>
                this.props.editUserInDB({
                  _id: this.props.user._id,
                  nom_prenom: this.props.user.nom_prenom
                    ? this.props.user.nom_prenom
                    : this.state.nom_prenom,
                  email: this.props.user.email
                    ? this.props.user.email
                    : this.state.email,
                  password: this.props.user.password
                    ? this.props.user.password
                    : this.state.password,
                  role: this.props.user.role,
                })
              }
            >
              Save changes
            </MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    user: state.auth,
  };
};
const dispatch = (dispatch) => {
  return {
    getUser: () => dispatch(getUser()),
    editUser: (el) => dispatch(editUserInDB(el)),
  };
};
export default connect(mapStateToProps, dispatch)(EditProfil);
