import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInput,
} from "mdbreact";

export class AddReservation extends Component {
  state = {
    modal14: false,
    dispo: [],
    time: "",
    Lundi: false,
    Mardi: false,
    Mercredi: false,
    Jeudi: false,
    Vendredi: false,
    Samedi: false,
    Dimanche: false,
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };
  render() {
    return (
      <div className="add-reservation">
        <MDBContainer>
          <MDBBtn color="primary" onClick={this.toggle(14)}>
            Confirmer votre r√©servation{" "}
          </MDBBtn>
          <MDBModal
            isOpen={this.state.modal14}
            toggle={this.toggle(14)}
            centered
          >
            <MDBModalHeader toggle={this.toggle(14)}>
              MDBModal title
            </MDBModalHeader>
            <MDBModalBody>
              <MDBInput
                onChange={(e) =>
                  this.setState({ adresse_client: e.target.value })
                }
                label="adresse"
                outline
              />
              <MDBInput
                onChange={(e) => this.setState({ tel_client: e.target.value })}
                label="num-tel"
                outline
              />
              <div class="md-form md-outline">
                <input
                  type="time"
                  id="default-picker"
                  class="form-control"
                  placeholder="Select time"
                  onChange={(e) => this.setState({ time: e.target.value })}
                />
                <label for="default-picker">Default Time Picker</label>
              </div>
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggle(14)}>
                Close
              </MDBBtn>
              <MDBBtn color="primary">Save changes</MDBBtn>
            </MDBModalFooter>
          </MDBModal>
        </MDBContainer>
      </div>
    );
  }
}

export default AddReservation;
