import axios from "axios";
import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  // LOGIN_SUCCESS,
  // LOGOUT
} from "./types";
// import setAuthToken from "./setToken";

export const register = (nom_prenom, email, password, role) => async (
  dispatch
) => {
  try {
    const res = await axios.post("http://localhost:8000/chef-d'oeuvre/authentif/register", {
      nom_prenom,
      email,
      password,
      role,
    });

    dispatch({ type: REGISTER_SUCCESS, payload: res.data });
    dispatch({
      type: USER_LOADED,
    });
  } catch (err) {
    const error = err.response.data.msg;
    error && alert((error));
  }
};
