import axios from "axios";

const setAuthToken = (token) => {
  if (token) {
    axios.defaults.headers.common["token"] = token; //autre forme d'object:["key"]=value
  } else {
    delete axios.defaults.headers.common["token"];
  }
};
export default setAuthToken;
