import React, { Component } from "react";

import {
  MDBCarousel,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBMask,
  MDBContainer,
  MDBIcon,
  MDBBtn
} from "mdbreact";
import { getAideFromDB } from "../actions/aideActionCreator";
import { getUser } from "../actions/auth";
import Navbar from "./navbar";
import { connect } from "react-redux";
import Connexion from "./connexion";

/******************************************************** ******/
export class Accueil extends Component {
  state = {
    jour: "",
    ville: "",
    modal: false,
  }

  componentDidMount() {
    this.props.getAideFromDB();
    this.props.getUser();
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  /************************************************************ */
  render() {

    const villes = ["Bab El Bhar", "Bab Souika", "Carthage","Cité El Khadra","Sidi Hassine","Sidi El Béchir","Séjoumi","Médina","Le Kram","Le Bardo","La Marsa","La Goulette","Hraïria","Ezzouhour","Ettahrir","Ouardia","El Omrane supérieur","El Omrane","El Menzah","El Kabaria","Djebel Jelloud"];
    //console.log("taw", this.props.user);
    return (
      <div className="accueil">
        <Navbar user={this.props.user} />
        {/**** * *****carousel *********/}
        <div className="caroussel">
          <MDBContainer>
            <MDBCarousel
              activeItem={1}
              length={3}
              showControls={true}
              showIndicators={true}
              className="z-depth-1"
            >
              <MDBCarouselInner>
                <MDBCarouselItem itemId="1">
                  <MDBView>
                    <img
                      className="d-block w-100"
                      src="https://mdbootstrap.com/img/Photos/Slides/img%20(68).jpg"
                      alt="First slide"
                    />
                    <MDBMask overlay="black-light" />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className="h3-responsive">Light mask</h3>
                    <p>First text</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>
                <MDBCarouselItem itemId="2">
                  <MDBView>
                    <img
                      className="d-block w-100"
                      src="https://mdbootstrap.com/img/Photos/Slides/img%20(6).jpg"
                      alt="Second slide"
                    />
                    <MDBMask overlay="black-strong" />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className="h3-responsive">Strong mask</h3>
                    <p>Second text</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>
                <MDBCarouselItem itemId="3">
                  <MDBView>
                    <img
                      className="d-block w-100"
                      src="https://mdbootstrap.com/img/Photos/Slides/img%20(9).jpg"
                      alt="Third slide"
                    />
                    <MDBMask overlay="black-slight" />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className="h3-responsive">Slight Mast</h3>
                    <p>Third text</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>
              </MDBCarouselInner>
            </MDBCarousel>
          </MDBContainer>
        </div>
        {/************ ************** les filtres ************************/}
        <div className="filtre container">
          {/* *********** filtre par délégation ***************/}
          <select
            className="browser-default custom-select"
            style={{ width: "200px" }}
            onChange={(e) => this.setState({ ville: e.target.value })}
          >
            <option>Choisir la Délégation</option>
            {villes.map((el) => (
              <option>{el}</option>
            ))}
            {/* <option value="">Choisir la Délégation</option>
            <option value="Bab El Bhar">Bab El Bhar</option>
            <option value="Bab Souika">Bab Souika</option>
            <option value="Carthage">Carthage</option>
            <option value="Cité El Khadra">Cité El Khadra</option>
            <option value="Sidi Hassine">Sidi Hassine</option>
            <option value="Sidi El Béchir">Sidi El Béchir</option>
            <option value="Séjoumi">Séjoumi</option>
            <option value="Médina">Médina</option>
            <option value="Le Kram">Le Kram</option>
            <option value="Le Bardo">Le Bardo</option>
            <option value="La Marsa">La Marsa</option>
            <option value="La Goulette">La Goulette</option>
            <option value="Hraïria">Hraïria</option>
            <option value="Ezzouhour ">Ezzouhour</option>
            <option value="Ettahrir">Ettahrir</option>
            <option value="Ouardia">Ouardia</option>
            <option value="El Omrane supérieur">El Omrane supérieur</option>
            <option value="El Omrane">El Omrane</option>
            <option value="El Menzah">El Menzah </option>
            <option value="El Kabaria">El Kabaria</option>
            <option value="Djebel Jelloud">Djebel Jelloud</option> */}
          </select>
          {/* ***********filtre par jour ***************/}

          <select
            className="browser-default custom-select"
            style={{ width: "200px" }}
            onChange={(e) => this.setState({ jour: e.target.value })}
          >
            <option value="">Choisir le jour</option>
            <option value="Lundi">Lundi</option>
            <option value="Mardi">Mardi</option>
            <option value="Mercredi">Mercredi</option>
            <option value="Jeudi">Jeudi</option>
            <option value="Vendredi">Vendredi</option>
            <option value="Samedi">Samedi</option>
            <option value="Dimanche">Dimanche</option>
          </select>
        </div>
        {/* ***********cards *******************/}
        {/* fonction filtre */}
        <div className="cards container">
          {this.props.aides
            .filter((jr) =>
              this.state.jour === "" ? jr : jr.dispo === this.state.jour
            )
            .filter((dl) =>
              this.state.ville === "" ? dl : dl.ville === this.state.ville
            )
            .map((el) => (
              <div className="flip-card">
                <div className="flip-card-inner">
                  <div className="flip-card-front">
                    <img
                      className="card-aide"
                      src={"http://localhost:8000/" + el.photo}
                      alt="Avatar"
                    />
                    <br></br>
                    <h2>{el.nom}</h2>
                    <hr className="line"></hr>
                    <h5>
                      Plus de détails <MDBIcon icon="angle-double-right" />
                    </h5>
                  </div>
                  <div className="flip-card-back">
                    <h1>{el.age}</h1>
                    <h4>{el.ville}</h4>
                    <h4>{el.exp}</h4>
                    {/* <MDBBtn outline color="info">
                      Réserver
                      <MDBIcon far icon="paper-plane" />
                    </MDBBtn> */}
           <MDBBtn gradient="blue" onClick={this.toggle}>
            Connexion
          </MDBBtn>
                  </div>
                </div>
              </div>
            ))}
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
    user: state.auth
  };
};
export default connect(mapStateToProps, { getAideFromDB, getUser })(Accueil);
