import React, { Component } from "react";
import { connect } from "react-redux";
import { getAideFromDB } from "../../actions/aideActionCreator";
import { getReservation } from "../../actions/reservationAction";
import { getUser } from "../../actions/auth";
import { Label, Table, Button } from "semantic-ui-react";

export class ReservationAttente extends Component {
  componentDidMount() {
    this.props.getUser();
    this.props.getAideFromDB();
    this.props.getUser() && this.props.getReservation();
  }
  render() {
    const reservations = this.props.reservation.filter(
      (el) => el.user._id.toString() === this.props.user._id.toString()
    );

    console.log("u", reservations);
    return <div className="liste-reservation container"></div>;
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
    user: state.auth,
    reservation: state.reservation,
  };
};

export default connect(mapStateToProps, {
  getAideFromDB,
  getReservation,
  getUser,
})(ReservationAttente);
