import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBInputGroup,MDBInput, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class Postuler extends Component {
state = {
  modal14: false,
  flipped1: false,

}
/****Modal function */
toggle = nr => () => {
  let modalNumber = 'modal' + nr
  this.setState({
    [modalNumber]: !this.state[modalNumber]
  });
}
/*****Card function */
handleFlipping = id => () => {
  const cardId = `flipped${id}`;
  this.setState({ [cardId]: !this.state[cardId] });
}

render() {
  return (
      <MDBContainer>
        <MDBBtn color="primary" onClick={this.toggle(14)}>MDBModal</MDBBtn>
        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}>MDBModal title</MDBModalHeader>
          {/* modal- body*/}
          <MDBModalBody>
          <MDBInput label="Nom Prénom" outline />
          <MDBInput label="Age" outline />
          <MDBInput label="Photo" outline />
          <MDBInputGroup
          containerClassName="mb-3"
          prepend="Sexe"
          inputs={
            <select className="browser-default custom-select">
              <option value="0">Choisir...</option>
              <option value="1">Femme</option>
              <option value="2">Homme</option>
            </select>
          }
        />
          <MDBInput label="Numéro téléphone" outline />
          <MDBInput label="Ville" outline />
          <MDBInput label="Disponibilité" outline />


          <MDBInput label="Expérience" outline />


          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(14)}>Close</MDBBtn>
            <MDBBtn color="primary"></MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default Postuler;
