import { GET_RESERVATION, ADD_RESERVATION } from "./types";
import axios from "axios";
export const getReservation = (user) => async (dispatch) => {
  try {
    const res = user
      ? await axios.get(
          `http://localhost:8000/chef-d'oeuvre/reservation/?user_id=${user}`
        )
      : await axios.get("http://localhost:8000/chef-d'oeuvre/reservation/");
    console.log(res.data);
    dispatch({
      type: GET_RESERVATION,
      payload: res.data,
    });
  } catch (error) {
    console.log(error);
  }
};
export const addAide = (payload) => {
  return {
    type: ADD_AIDE,
    payload,
  };
};
export const addAideToDB = (el) => {
  // console.log(el);
  return (dispatch) =>
    axios
      .post("http://localhost:8000/chef-d'oeuvre/aides/add", el)
      .then((res) => {
        dispatch(addAide(res.data), window.location.reload());
      });
};
