import { ADD_AIDE, GET_AIDE, EDIT_AIDE} from "./types";
import axios from "axios";
/*************************** POST ************************ */
export const addAide = (payload) => {
  return {
    type: ADD_AIDE,
    payload,
  };
};
export const addAideToDB = (el) => {
  console.log(el);
  return (dispatch) =>
    axios
      .post("http://localhost:8000/chef-d'oeuvre/aides/add", el)
      .then((res) => dispatch(addAide(res.data)));
};

/*************************** EDIT ****************************/
export const editAide = (payload) => {
  return {
    type: EDIT_AIDE,
    payload,
  };
};
