//         dispatch(editAide(res.data), window.location.reload(false))
//       );
// };
/*******************************GET****************************** */
export const getAide = (payload) => {
  return {
    type: GET_AIDE,
    payload,
  };
};
export const getAideFromDB = () => {
  return (dispatch) =>
    axios
      .get("http://localhost:8000/chef-d'oeuvre/aides/getallaide")
      .then((res) => dispatch(getAide(res.data)));
};
