import React, { Component } from "react";
import "../App.css";
import { BrowserRouter as Router } from "react-router-dom";
import { MDBBtn } from "mdbreact";
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavbarToggler,
  MDBCollapse,
} from "mdbreact";

export class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
    };
    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse,
    });
  }

  render() {
    const bgPink = { backgroundColor: "#9cc1ff" };
    const container = { height: 1300 };
    return (
      <div>
        <Router>
          <header>
            <MDBNavbar style={bgPink} dark expand="md" scrolling fixed="top">
              <MDBNavbarBrand href="/">
                <strong>Navbar</strong>
              </MDBNavbarBrand>
              <MDBNavbarToggler onClick={this.onClick} />
              <MDBCollapse isOpen={this.state.collapse} navbar>
                <MDBNavbarNav left></MDBNavbarNav>
                <MDBNavbarNav right>
                <MDBBtn color="secondary">Connexion</MDBBtn>
                <MDBBtn outline color="secondary">Inscription</MDBBtn>
                </MDBNavbarNav>
              </MDBCollapse>
            </MDBNavbar>
          </header>
        </Router>
        <MDBContainer style={container} className="text-center mt-5 pt-5">
          <h2>This Navbar is fixed</h2>
          <h5>
            It will always stay visible on the top, even when you scroll down
          </h5>
          <br />
          <p>
            Full page intro with background image will be always displayed in
            full screen mode, regardless of device
          </p>
        </MDBContainer>
      </div>
    );
  }
}

export default Navbar;

