          <div class="row trust__items row">
            <div class="col-md-3">
              <div class="trust text-center">
                <p className="text-size">Plus de</p>
                <h3 className="text-center" style={{ fontSize: "25px" }}>{el.nom_prenom}</h3>

                <br></br>
                <p class="trust--bold">100 000</p>
                <br></br>
                <p className="text-size">foyers nettoyés en Tunisie</p>
              </div>
            </div>
            <div class="col-md-3 columns">
              <div class="trust text-center ">
                <p className="text-size">Déjà</p>
                <br></br>
                <p class="trust--bold">80 000</p>
                <br></br>
                <p className="text-size">clients satisfaits</p>
              </div>
            </div>
            <div class="col-md-3">
              <div class="trust text-center">
              <h1 className="text-center" style={{ fontSize: "30px" }}>
                  {el.rating === 5 ? (
                    <>
                    {<i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>}
                      <i class="fas fa-star"  style={{color:"rgba(29, 210, 177, 0.7)"}}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating === 4 ? (
                    <>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating === 3 ? (
                    <>
                      {" "}
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating === 3 ? (
                    <>
                      {" "}
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating == 2 ? (
                    <>
                      {" "}
                      <i class="fas fa-star"  style={{ color: "blrgba(29, 210, 177, 0.7)ue" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : (
                    <i class="fas fa-broom icon" style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                  )}{" "}
                </h1>
                <p className="text-size">Notre Moyenne de</p>
                <br></br>
                <h3 className="text-center">"{el.msg}"</h3>

                <p class="trust--bold">4.7 / 5</p>
                <br></br>
                <p className="text-size">étoiles</p>
              </div>
            </div>
            <div class="col-md-3">
              <div class="trust">
                <p className="text-center text-size  ">Disponible dans</p>
                <br></br>
                <p class="trust--bold text-center">&gt;10 </p>
                <br></br>
                <p className="text-center text-size">villes en Grand Tunis</p>
              </div>
            </div>
          </div>
        </section>
        {/* rating */}
              {/* <div className="item">
                <h3 className="text-center" style={{ fontSize: "25px" }}>{el.nom_prenom}</h3>
