                  {el.rating === 5 ? (
                    <>
                    {<i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>}
                      <i class="fas fa-star"  style={{color:"rgba(29, 210, 177, 0.7)"}}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating === 4 ? (
                    <>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating === 3 ? (
                    <>
                      {" "}
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating === 3 ? (
                    <>
                      {" "}
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : el.rating == 2 ? (
                    <>
                      {" "}
                      <i class="fas fa-star"  style={{ color: "blrgba(29, 210, 177, 0.7)ue" }}></i>
                      <i class="fas fa-star"  style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>{" "}
                    </>
                  ) : (
                    <i class="fas fa-broom icon" style={{ color: "rgba(29, 210, 177, 0.7)" }}></i>
                  )}{" "}
                </h1>

                <h3 className="text-center">"{el.msg}"</h3>
              </div> */}
            </>
          ))}
        {/* </OwlCarousel> */}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    rating: state.rating,
  };
};

export default connect(mapStateToProps, { getRatingFromDB })(CarouselRating);
