import { ADD_AIDE } from "./type";
import axios from "axios";
export const addAide = (payload) => {
  return {
    type: ADD_AIDE,
    payload,
  };
};
export const addAideToDB = (el) => {
  return (dispatch) =>
    axios
      .post("http://localhost:4000/aides", el)
      .then((res) =>
        dispatch(addAide(res.data), window.location.reload(false))
      );
};
