import React, { Component } from "react";
import "../App.css";
import { BrowserRouter as Router } from "react-router-dom";
import {
  MDBNavbar,
  MDBNavItem,
  MDBNavLink,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavbarToggler,
  MDBCollapse,
  MDBIcon
} from "mdbreact";

import Inscription from "./inscription";
import Connexion from "./connexion";

import { logout } from "../actions/auth";
import { connect } from "react-redux";

import { login } from "../actions/auth";

export class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
      modal: false,
    };
    this.onClick = this.onClick.bind(this);
  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse,
    });
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  render() {
    console.log("z", this.props.user);
    const bgPink = { backgroundColor: "#ffffff" };
    return (
      <div>
        <Router>
          <header>
            <MDBNavbar style={bgPink} dark expand="md" scrolling fixed="top">
              <MDBNavbarBrand href="/">
                <strong style={{ color: "blue" }}>Navbar</strong>
              </MDBNavbarBrand>
              <MDBNavbarToggler onClick={this.onClick} />
              <MDBCollapse isOpen={this.state.collapse} navbar>
                <MDBNavbarNav left>

                  <MDBNavItem active>
                                      <MDBIcon icon="home" fixed />

                    <MDBNavLink style={{ color: "blue" }} to="/">
                      Accueil
                    </MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>

                    <MDBNavLink style={{ color: "blue" }} to="/aides">
                      Aides ménagères
                    </MDBNavLink>
                  </MDBNavItem>
                  <MDBNavItem>
                    <MDBNavLink style={{ color: "blue" }} to="/client">
                      Client
                    </MDBNavLink>
                  </MDBNavItem>
                </MDBNavbarNav>
                <MDBNavbarNav right>
                  {this.props.user.role ? (
                    <button onClick={() => this.props.logout()}>logout</button>
                  ) : (
                    <Connexion />
                  )}
                  {this.props.user.role ? "" : <Inscription />}
                </MDBNavbarNav>
              </MDBCollapse>
            </MDBNavbar>
          </header>
        </Router>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    signin: (el) => dispatch(login(el)),
    logout: () => dispatch(logout()),
  };
};

export default connect(null, mapDispatchToProps)(Navbar);
