import React, { Component } from "react";
import "../App.css";

import {
  MDBContainer,
  MDBIcon,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
} from "mdbreact";
import tof from "./images/ph1.png";
import { login } from "../actions/auth";
import { getAideFromDB } from "../actions/aideActionCreator";
import { getUser } from "../actions/auth";
import { connect } from "react-redux";
import Pub from "./publicité";
import AddReservation from "./dashboardClient/choisirReservation";

/******************************************************** ******/
export class Accueil extends Component {
  state = {
    jour: "",
    ville: "",
    service: "",
    modal: false,
    modal3: false,
    dispo: [],
    Lundi: false,
    Mardi: false,
    Mercredi: false,
    Jeudi: false,
    Vendredi: false,
    Samedi: false,
    Dimanche: false,
  };

  componentDidMount() {
    this.props.getAideFromDB();
    this.props.getUser();
  }
  toggleReservation = () => {
    this.setState({
      modal3: !this.state.modal3,
    });
  };
  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  /************************************************************ */
  render() {
    const villes = [
      "Bab El Bhar",
      "Bab Souika",
      "Carthage",
      "Cité El Khadra",
      "Sidi Hassine",
      "Sidi El Béchir",
      "Séjoumi",
      "Médina",
      "Le Kram",
      "Le Bardo",
      "La Marsa",
      "La Goulette",
      "Hraïria",
      "Ezzouhour",
      "Ettahrir",
      "Ouardia",
      "El Omrane supérieur",
      "El Omrane",
      "El Menzah",
      "El Kabaria",
      "Djebel Jelloud",
    ];
    //console.log("taw", this.props.user);
    return (
      <div className="accueil">
        {/* <div className="text">
          <h1>
            {" "}
            <i class="fa fa-quote-left"></i>
            <span class="black">
              <p>
                Un service d’aide à domicile <strong>de qualité</strong>
              </p>
            </span>
            <i class="fa fa-quote-right"></i>
          </h1>
        </div> */}
        <Pub />
        {/**** * *****carousel *********/}

        {/* text */}
        <div className="text">
          <h1>
            {" "}
            <i class="fa fa-quote-left"></i>
            <span class="black">
              <p> Réservez votre ménage à domicile en un simple clic</p>
            </span>
            <i class="fa fa-quote-right"></i>
          </h1>
        </div>
        {/************ ************** les filtres ************************/}
        <div className="filtre container">
          {/* *********** filtre par délégation ***************/}
          <select
            className="browser-default custom-select"
            style={{ width: "200px" }}
            onChange={(e) => this.setState({ ville: e.target.value })}
          >
            <option>Choisir la Délégation</option>
            {villes.map((el) => (
              <option>{el}</option>
            ))}
          </select>
          {/* ***********filtre par jour ***************/}

          <select
            className="browser-default custom-select"
            style={{ width: "200px" }}
            onChange={(e) => this.setState({ jour: e.target.value })}
          >
            <option value="">Choisir le jour</option>
            <option value="Lundi">Lundi</option>
            <option value="Mardi">Mardi</option>
            <option value="Mercredi">Mercredi</option>
            <option value="Jeudi">Jeudi</option>
            <option value="Vendredi">Vendredi</option>
            <option value="Samedi">Samedi</option>
            <option value="Dimanche">Dimanche</option>
          </select>

          {/* ***********filtre par type de service ***************/}
          <select
            className="browser-default custom-select"
            style={{ width: "200px" }}
            onChange={(e) => this.setState({ service: e.target.value })}
          >
            <option value="">Choisir le service</option>
            <option
              value="Nettoyage domestique
"
            >
              Nettoyage domestique
            </option>
            <option
              value="Nettoyage de bureaux
"
            >
              Nettoyage de bureaux
            </option>
            <option
              value="Nettoyage de fin de location
"
            >
              Nettoyage de fin de location
            </option>
            <option
              value="Nettoyage des vitres
"
            >
              Nettoyage des vitres
            </option>
            <option
              value="Nettoyage de tapis
"
            >
              Nettoyage de tapis
            </option>
            <option
              value="Nettoyage des sols durs
 
"
            >
              Nettoyage des sols durs
            </option>
          </select>
        </div>
        {/* tof */}
        <img src={tof} alt="tof" />

        {/* ***********cards *******************/}
        {/* fonction filtre */}
        <div className="cards container">
          {this.props.aides

            .filter((jr) =>
              this.state.jour === "" ? jr : jr.dispo === this.state.jour
            )
            .filter((dl) =>
              this.state.ville === "" ? dl : dl.ville === this.state.ville
            )
            .filter((serv) =>
              this.state.service === ""
                ? serv
                : serv.service === this.state.service
            )

            .map((el) => (
              <div className="flip-card">
                <div className="flip-card-inner">
                  <div className="flip-card-front">
                    <img
                      className="card-aide"
                      src={"http://localhost:8000/" + el.photo}
                      alt="Avatar"
                    />
                    <br></br>
                    <h2>{el.nom}</h2>

                    <hr className="line"></hr>
                    <h5>
                      Plus de détails <MDBIcon icon="angle-double-right" />
                    </h5>
                  </div>
                  <div className="flip-card-back">
                    <h1>{el.age}</h1>
                    <h4>{el.ville}</h4>
                    <h4>{el.exp}</h4>
                    <h4>{el.service}</h4>
                    <h4>{el.dispo}</h4>
                    <h4>{el.sexe}</h4>

                    {!this.props.user.role &&
                    this.props.user.role !== "Aide ménagère" ? (
                      <MDBBtn
                        gradient="blue"
                        onClick={this.toggle}
                        style={{ borderRadius: "20px" }}
                      >
                        Réserver
                        <MDBIcon far icon="paper-plane" className="ml-2" />
                      </MDBBtn>
                    ) : this.props.user.role === "Client" ? (
                      // add réservation
                      <MDBBtn color="primary" onClick={this.toggleReservation}>
                        Confirmer votre réservation{" "}
                      </MDBBtn>
                    ) : (
                      ""
                    )}
                  </div>
                </div>
              </div>
            ))}
        </div>
        {/********************* * modal connexion ***************************************/}

        <div className="connexion">
          <MDBContainer autoWidth="false">
            <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
              <MDBModalHeader toggle={this.toggle}>Se connecter</MDBModalHeader>
              <MDBModalBody>
                <MDBContainer>
                  <MDBRow>
                    <MDBCol md="6">
                      <form>
                        <div className="grey-text">
                          <i
                            class="fas fa-envelope"
                            style={{ fontSize: "30px" }}
                          ></i>
                          <MDBInput
                            style={{ color: "black" }}
                            label="Adresse email"
                            group
                            type="email"
                            validate
                            error="wrong"
                            success="right"
                            onChange={(e) =>
                              this.setState({ email: e.target.value })
                            }
                          />
                          <i
                            class="fas fa-lock"
                            style={{ fontSize: "30px" }}
                          ></i>
                          <MDBInput
                            style={{ color: "black" }}
                            label="Mot de passe"
                            group
                            type="password"
                            validate
                            error="wrong"
                            success="right"
                            onChange={(e) =>
                              this.setState({ password: e.target.value })
                            }
                          />
                        </div>
                      </form>
                    </MDBCol>
                  </MDBRow>
                </MDBContainer>
              </MDBModalBody>
              <MDBModalFooter>
                <MDBBtn color="secondary" onClick={this.toggle}>
                  Fermer{" "}
                </MDBBtn>
                <MDBBtn
                  gradient="blue"
                  onClick={() =>
                    this.props.signin({
                      email: this.state.email,
                      password: this.state.password,
                    })
                  }
                >
                  Se connecter
                  <MDBIcon far icon="paper-plane" className="ml-2" />
                </MDBBtn>
              </MDBModalFooter>
            </MDBModal>
          </MDBContainer>
        </div>
        {/*************************** modal pour confirmation de reservation ***********************/}
        <MDBContainer>
          <MDBModal
            isOpen={this.state.modal3}
            toggle={this.toggleReservation}
            centered
          >
            <MDBModalHeader toggle={this.toggleReservation}>
              MDBModal title
            </MDBModalHeader>
            <MDBModalBody>
              <MDBInput
                onChange={(e) =>
                  this.setState({ adresse_client: e.target.value })
                }
                label="adresse"
                outline
              />
              <MDBInput
                onChange={(e) => this.setState({ tel_client: e.target.value })}
                label="num-tel"
                outline
              />
              <MDBDropdown dropright>
                <MDBDropdownToggle caret color="primary">
                  Disponibilité
                </MDBDropdownToggle>
                <MDBDropdownMenu basic>
                  {/* {this.props.aides.dispo.map((el) => (
                    <MDBDropdownItem>
                      <input
                        type="checkbox"
                        value="Lundi"
                        class="custom-control-input"
                        id="defaultUnchecked"
                      />
                      <label
                        class="custom-control-label"
                        for="defaultUnchecked"
                      >
                        {el}
                      </label>
                    </MDBDropdownItem>
                  ))} */}
                  {/* <MDBDropdownItem>
                    {" "}
                    <div class="custom-control custom-checkbox">
                      <input
                        type="checkbox"
                        value="Lundi"
                        class="custom-control-input"
                        id="defaultUnchecked"
                        onClick={() =>
                          this.setState({ Lundi: !this.state.Lundi })
                        }
                      />
                      <label
                        class="custom-control-label"
                        for="defaultUnchecked"
                      >
                        Lundi
                      </label>
                    </div>
                  </MDBDropdownItem> */}
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggleReservation}>
                Close
              </MDBBtn>
              <MDBBtn color="primary">Save changes</MDBBtn>
            </MDBModalFooter>
          </MDBModal>
        </MDBContainer>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    aides: state.aides,
    user: state.auth,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    signin: (el) => dispatch(login(el)),
    getUser: () => dispatch(getUser()),
    getAideFromDB: () => dispatch(getAideFromDB()),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Accueil);

