import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInputGroup,
  MDBInput,MDBCol, MDBRow, MDBCard, MDBCardUp, MDBCardBody, MDBAvatar, MDBRotatingCard, MDBIcon
} from "mdbreact";

class Postuler extends Component {
  state = {
    modal14: false, 
    flipped1: false,
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };
  handleFlipping = id => () => {
    const cardId = `flipped${id}`;
    this.setState({ [cardId]: !this.state[cardId] });
  }

  render() {
    return (
      <MDBContainer>
        <MDBBtn color="primary" onClick={this.toggle(14)}>
          MDBModal
        </MDBBtn>
        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}>
            MDBModal title
          </MDBModalHeader>
          <MDBModalBody>
            <MDBInput label="Nom Prénom" outline />
            <MDBInput label="Age" outline />
            <MDBInput label="Photo" outline />
            <MDBInputGroup
              containerClassName="mb-3"
              prepend="Sexe"
              inputs={
                <select className="browser-default custom-select">
                  <option value="0">Choisir...</option>
                  <option value="1">Femme</option>
                  <option value="2">Homme</option>
                </select>
              }
            />
            <MDBInput label="Numéro téléphone" outline />
            <MDBInput label="Ville" outline />
            <MDBInput label="Disponibilité" outline />

            <MDBInput label="Expérience" outline />
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(14)}>
              Close
            </MDBBtn>
            <MDBBtn color="primary">Save changes</MDBBtn>
          </MDBModalFooter>
        </MDBModal>

        <MDBCol md='4'>
        <MDBCard testimonial>
          <MDBCardUp gradient='aqua' />
          <MDBAvatar className='mx-auto white'>
            <img
              src='https://mdbootstrap.com/img/Photos/Avatars/img%20%2831%29.jpg'
              alt=''
            />
          </MDBAvatar>
          <MDBCardBody>
            <h4 className='card-title'>Martha Smith</h4>
            <hr />
            <p>
              <MDBIcon icon='quote-left' /> Lorem ipsum dolor sit amet,
              consectetur adipisicing elit. Eos, adipisci{' '}
              <MDBIcon icon='quote-right' />
            </p>
          </MDBCardBody>
        </MDBCard>
      </MDBCol>
      </MDBContainer>
    );
  }
}

export default Postuler;
