import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBInput,
} from "mdbreact";
import { connect } from "react-redux";
import { editUserInDB, getUser } from "../actions/auth";

export class EditProfil extends Component {
  state = {
    modal: false,
  };

  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  componentDidMount() {
    this.props.getUser();
  }
  /************************************************************** ******/

  render() {
    return (
      <div className="edit-profil">
        <MDBContainer>
          <MDBBtn
            outline
            color="info"
            onClick={this.toggle}
            style={{ borderRadius: "20px" }}
          >
            Modifier{" "}
          </MDBBtn>
          <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
            <MDBModalHeader toggle={this.toggle}>
              Modifier votre profil{" "}
            </MDBModalHeader>
            <MDBModalBody>
              <MDBContainer>
                <MDBRow>
                  <MDBCol md="6">
                    <form>
                      <div className="grey-text">
                        <i class="fas fa-user" style={{ fontSize: "30px" }}></i>
                        <MDBInput
                          style={{ color: "black" }}
                          label="Nom-Prénom"
                          group
                          type="text"
                          validate
                          error="wrong"
                          success="right"
                          onChange={(e) =>
                            this.setState({ nom_prenom: e.target.value })
                          }
                        />

                        <i
                          class="fas fa-envelope"
                          style={{ fontSize: "30px" }}
                        ></i>
                        <MDBInput
                          style={{ color: "black" }}
                          label="Adresse email"
                          group
                          validate
                          error="wrong"
                          success="right"
                          onChange={(e) =>
                            this.setState({ email: e.target.value })
                          }
                        />
                        <i class="fas fa-lock" style={{ fontSize: "30px" }}></i>
                        <MDBInput
                          style={{ color: "black" }}
                          label="Mot de passe"
                          group
                          validate
                          error="wrong"
                          type="password"
                          success="right"
                          onChange={(e) =>
                            this.setState({ password: e.target.value })
                          }
                        />
                        <select
                          className="browser-default custom-select"
                          style={{ width: "200px" }}
                          onChange={(e) =>
                            this.setState({ role: e.target.value })
                          }
                        >
                          <option>Choisir catégorie</option>
                          <option value="Client">Client</option>
                          <option value="Aide ménagère">Aide ménagère</option>
                        </select>
                      </div>
                    </form>
                  </MDBCol>
                </MDBRow>
              </MDBContainer>
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggle}>
                Fermer{" "}
              </MDBBtn>
              <MDBBtn
                gradient="blue"
                onClick={() =>
                  this.props.editUserInDB({
                    nom_prenom: this.state.nom_prenom,
                    email: this.state.email,
                    password: this.state.password,
                    role: this.state.role,
                  })
                }
              >
                Modifier <MDBIcon far icon="paper-plane" className="ml-2" />
              </MDBBtn>
            </MDBModalFooter>
          </MDBModal>
        </MDBContainer>
        {this.props.user.map((el) => (
          <div>
            <div class="card-container">
              <span class="pro">PRO</span>
              <img
                class="round"
                src={"http://localhost:8000/" + el.photo}
                style={({ width: "200px" }, { height: "200px" })}
                alt="user"
              />
              <h3>{el.nom}</h3>
              <h6>{el.ville}</h6>

              <h6>{el.age}</h6>
              <h6>{el.exp}</h6>
              <p>
                {el.service}
                <br /> front-end developer
              </p>
              <div class="buttons">
                {!this.props.user.role &&
                this.props.user.role !== "Aide ménagère" ? (
                  <button class="primary" onClick={this.toggle}>
                    Connexion
                  </button>
                ) : this.props.user.role === "Client" ? (
                  //  / add réservation
                  <AddReservation el={el} />
                ) : (
                  ""
                )}
              </div>

              <div class="skills">
                <h6>{el.sexe}</h6>{" "}
                <ul>
                  {el.dispo.map((el) => (
                    <li>{el}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    user: state.auth,
  };
};

export default connect(mapStateToProps, { getUser, editUserInDB })(EditProfil);
