import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInput,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
} from "mdbreact";
import { connect } from "react-redux";
import { addReservationToDB } from "../../actions/reservationAction";
export class AddReservation extends Component {
  state = {
    modal14: false,
    dispo: [],
    time: "",
    Lundi: false,
    Mardi: false,
    Mercredi: false,
    Jeudi: false,
    Vendredi: false,
    Samedi: false,
    Dimanche: false,
    modal3: false,
  };
  toggleReservation = () => {
    this.setState({
      modal3: !this.state.modal3,
    });
  };
  render() {
    const { el } = this.props;
    console.log("olfa", this.props.el);
    return (
      <div className="add-reservation">
        <MDBContainer>
          <button class="primary ghost" onClick={this.toggleReservation}>
            Following{" "}
          </button>
          <MDBModal
            isOpen={this.state.modal3}
            toggle={this.toggleReservation}
            centered
          >
            <MDBModalHeader toggle={this.toggleReservation}>
              MDBModal title
            </MDBModalHeader>
            <MDBModalBody>
              <MDBInput
                onChange={(e) =>
                  this.setState({ adresse_client: e.target.value })
                }
                label="adresse"
                outline
              />
              <MDBInput
                onChange={(e) => this.setState({ tel_client: e.target.value })}
                label="num-tel"
                outline
              />
              {/* <MDBDropdown dropright>
                <MDBDropdownToggle caret color="primary">
                  Disponibilit√©
                </MDBDropdownToggle>
                <MDBDropdownMenu basic>
                  {this.props.el.dispo
                    .filter((el) => el !== null)
                    .map((el) => (
                      <MDBDropdownItem>
                        <input
                          type="checkbox"
                          value="Lundi"
                          class="custom-control-input"
                          id="defaultUnchecked"
                        />
                        <label
                          class="custom-control-label"
                          for="defaultUnchecked"
                        >
                          {el}
                        </label>
                      </MDBDropdownItem>
                    ))}
                </MDBDropdownMenu>
              </MDBDropdown> */}
            </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggleReservation}>
                Close
              </MDBBtn>
              <MDBBtn
                color="primary"
                onClick={() =>
                  this.props.addReservation({
                    nom: el.nom,
                  })
                }
              >
                Save changes
              </MDBBtn>
            </MDBModalFooter>
          </MDBModal>
        </MDBContainer>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch) => ({
  addReservation: (el) => dispatch(addReservationToDB(el)),
});
export default connect(null, mapDispatchToProps)(AddReservation);
