import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInputGroup,
  MDBInput,
  MDBIcon,
} from "mdbreact";
import axios from "axios";

import { addAideToDB } from "../../actions/aideActionCreator";
import { connect } from "react-redux";

/**************************************************************** */

class Postuler extends Component {
  state = {
    modal14: false,
    selectedFile: [],
  };


  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };
  /*************function upload img*********** */
  fileSelectedHandler = (e) => {
    this.setState({ selectedFile: e.target.files[0] });
  };

  uploadHandler = () => {
    const fd = new FormData();
    fd.append("image", this.state.selectedFile);
    axios
      .post("http://localhost:8000/", fd)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };
/************************************************************************* */
  render() {
    return (
      <MDBContainer>
        {/* *******************modal******************* */}
        <MDBBtn gradient="blue" onClick={this.toggle(14)}>
          Postuler maintenant
        </MDBBtn>
        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}>
            Postuler maintenant{" "}
          </MDBModalHeader>
          <MDBModalBody>
            <MDBInput
              onChange={(e) => this.setState({ Nom_Prénom: e.target.value })}
              label="Nom Prénom"
              outline
            />
            <MDBInput
              onChange={(e) => this.setState({ Age: e.target.value })}
              label="Age"
              outline
            />
             <select
            className="browser-default custom-select"
            onChange={(e) => this.setState({ ville: e.target.value })}

          >
            <option >Choisir la Délégation</option>
            <option value="Bab El Bhar">Bab El Bhar</option>
            <option value="Bab Souika">Bab Souika</option>
            <option value="Carthage">Carthage</option>
            <option value="Cité El Khadra">Cité El Khadra</option>
            <option value="Sidi Hassine">Sidi Hassine</option>
            <option value="Sidi El Béchir">Sidi El Béchir</option>
            <option value="Séjoumi">Séjoumi</option>
            <option value="Médina">Médina</option>
            <option value="Le Kram">Le Kram</option>
            <option value="Le Bardo">Le Bardo</option>
            <option value="La Marsa">La Marsa</option>
            <option value="La Goulette">La Goulette</option>
            <option value="Hraïria">Hraïria</option>
            <option value="Ezzouhour ">Ezzouhour</option>
            <option value="Ettahrir">Ettahrir</option>
            <option value="Ouardia">Ouardia</option>
            <option value="El Omrane supérieur">El Omrane supérieur</option>
            <option value="El Omrane">El Omrane</option>
            <option value="El Menzah">El Menzah </option>
            <option value="El Kabaria">El Kabaria</option>
            <option value="Djebel Jelloud">Djebel Jelloud</option>
          </select>
            <MDBInput
              type="file"
              onChange={(e) => this.fileSelectedHandler(e)}
              outline
            />
            <MDBBtn color="dark" onClick={() => this.uploadHandler()}>
              Télécharger
            </MDBBtn>
            <MDBInputGroup
              containerClassName="mb-3"
              prepend="Sexe"
              inputs={
                <select
                  onChange={(e) => this.setState({ sexe: e.target.value })}
                  className="browser-default custom-select"
                >
                  <option value="0">Choisir...</option>
                  <option value="femme">Femme</option>
                  <option value="homme">Homme</option>
                </select>
              }
            />
            <MDBInput
              onChange={(e) => this.setState({ Num: e.target.value })}
              label="Numéro téléphone"
              outline
            />

            <select
            className="browser-default custom-select"
            onChange={(e) => this.setState({ dispo: e.target.value })}

          >
            <option >Choisir le jour</option>
            <option value="Lundi">Lundi</option>
            <option value="Mardi">Mardi</option>
            <option value="Mercredi">Mercredi</option>
            <option value="Jeudi">Jeudi</option>
            <option value="Vendredi">Vendredi</option>
            <option value="Samedi">Samedi</option>
            <option value="Dimanche">Dimanche</option>
          </select>

            <MDBInput
              onChange={(e) => this.setState({ Exp: e.target.value })}
              label="Expérience"
              outline
            />
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle(14)}>
              Close
            </MDBBtn>
            <MDBBtn
              gradient="blue"
              onClick={() =>
                this.props.addAide({
                  nom: this.state.Nom_Prénom,
                  age: this.state.Age,
                  photo: this.state.selectedFile.name,
                  ville: this.state.Ville,
                  exp: this.state.Exp,
                  dispo: this.state.dispo,
                  num: this.state.Num,
                  sexe: this.state.sexe,
                })
              }
            >
              Postuler
              <MDBIcon far icon="paper-plane" className="ml-2" />
            </MDBBtn>
          </MDBModalFooter>
        </MDBModal>
        {/*************** **** card ****************/}
        <h1>Card Flip with Text</h1>
        <h3>Hover over the image below:</h3>

        <div className="flip-card">
          <div className="flip-card-inner">
            <div className="flip-card-front">
              <img
                className="card-aide"
                src="https://resize.prod.femina.ladmedia.fr/rblr/652,438/img/var/2015-09/comment-devenir-une-personne-solaire-0e95b16c5fefae293635ad5b6ce2b1198537e371.jpg"
                alt="Avatar"
              />
              <p>kkk</p>
            </div>
            <div className="flip-card-back">
              <h1>John Doe</h1>
              <p>Architect & Engineer</p>
              <p>We love that guy</p>
            </div>
          </div>
        </div>
      </MDBContainer>
    );
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    addAide: (el) => dispatch(addAideToDB(el)),
  };
};

export default connect(null, mapDispatchToProps)(Postuler);
