import {
    REGISTER_SUCCESS,
    //REGISTER_FAIL,
    USER_LOADED,
    LOGIN_SUCCESS,
    // LOGIN_FAIL,
    LOGOUT
  } from '../actions/types';
  const initialState = {
    token: localStorage.getItem('token'),
    user: null
  };
  const authReducer = (state = initialState, action) => {
    const { type, payload } = action;
    switch (type) {
      case REGISTER_SUCCESS:
      case LOGIN_SUCCESS:
        localStorage.setItem('token', payload.token);
       break;


      case USER_LOADED:
        return {
          ...state,        
          user: payload
        };
        case LOGOUT:

            localStorage.removeItem('token')

      default:
        return state;
    }
  };
  export default authReducer;
